    /*
    if (std::isnan(testResult)) {
	isCorrect = false;
    }
    */

    if (IS_ROOT(rank) && correctResult != testResult) {
        isCorrect = false;
    }
    BCAST_PTR(&isCorrect, 1, CXX_BOOL);
    if (!isCorrect) {
        return false;
    }

    return true;

